import React, { useState, useEffect } from 'react';
import Sidebar from '../Core/Sidebar/sidebar';
import Header from '../Core/Headers/Headers';
import styled from 'styled-components';
import { getDepts, getPos } from '../Admin/helper/adminapicalls';
import PropTypes from 'prop-types';
const Form = styled.form`
	background-color: #e9e7e6;
	margin-left: 130px;
	margin-right: 100px;
	margin-top: 50px;
	border-radius: 10px;
`;
const Button = styled.button`margin-left: 40%;`;
const AddNew = (props) => {
	const [ employee, setEmployee ] = useState({
		name: '',
		email: '',
		phone: '',
		address: '',
		department: '',
		position: '',
		salary: false,
		departments: [],
		positions: []
	});
	//destructure employee
	var { name, email, phone, address, department, position, salary, departments, positions } = employee;
	//load values on load

	useEffect(() => {
		const loadDept = () => {
			getDepts().then((data) => {
				if (data !== undefined) {
					console.log('ok');
					setEmployee({ ...employee, departments: data });
				}
				//console.log(data);
			});
		};
		// eslint-disable-next-line no-restricted-globals

		loadDept();
	}, []);

	const addNewForm = () => (
		<Form>
			<div className="addform">
				<label class="form-label">Full Name</label>
				<input
					type="text"
					class="form-control"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Name"
				/>
				<label class="form-label">Email</label>
				<input
					type="Email"
					class="form-control"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Email"
				/>
				<label class="form-label">Phone</label>
				<input
					type="text"
					class="form-control"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Phone"
				/>
				<label class="form-label">Address</label>
				<textarea class="form-control" placeholder="Address" id="floatingTextarea2" rows="3" />
				<select className="form-control" placeholder="Category">
					<option>Department</option>
					{departments && departments.length > 0 ? (
						departments.map((dept, index) => <option key={index}>{dept.name}</option>)
					) : (
						<option>No Department Found</option>
					)}
				</select>
				<select className="form-control " placeholder="Category">
					<option selected>Position</option>
					<option value="1">One</option>
					<option value="2">Two</option>
					<option value="3">Three</option>
				</select>
				<input class="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
				<label class="form-check-label " for="flexCheckDefault">
					Salary Paid
				</label>
				<br />
				<Button className="btn btn-success">Submit</Button>
			</div>
		</Form>
	);
	return (
		<div>
			<Header />
			<div className="row">
				<div className="col-sm-3">
					<Sidebar />
				</div>
				<div className="col-sm-7 ">{addNewForm()}</div>
			</div>
		</div>
	);
};

AddNew.propTypes = {};

export default AddNew;
