import React, { useState } from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
const Table = styled.div``;
	const Row = styled.div`margin-top: 10px;`;
	const HR = styled.hr`
	border: 0.125rem solid white;
	margin-top = 5px ;
	`;  
	const table = styled.table`border: 1px solid black;`;
	const Label = styled.label`padding-top: 1rem;`;

class Department_List extends React.Component  {
	constructor(props){
		super(props) ;
		this.state = {
			newitem: '', 
			list : [] 
		} ;
	} 
	 addDept = (value) => {
		if(value!== ''){
			const newdpt = {
				id : Date.now() ,
				name : value 
			};
			const list = [...this.state.list] ;
			list.push(newdpt) ; 
			this.setState({
				list,
				name : ''
			})

		}
	deletDpt = (id) =>{
		const list = [...this.state.list.filter(item=>{
			return(item.id!==id)
		})]
		this.setState({list})
	}

	}
	 changeHandler = ( value) =>{
		this.setState({name : value}) ;
	}
	render(){
	return ( 
		<div>
		<div className="container">
			<Row className="row no-gutters">
				<div className="col-sm-2  dptform">
					<div class="cards">
						<div class="form-group">
							<Label for="comment">Department Form</Label>
							<HR />
							<h6>Name</h6>
							<input
								type="text"
								placeholder="Type..."
								class="form-control"
								rows="2" 
								value = {this.state.name}  
								onChange = {e => this.changeHandler(e.target.value)}
								id="comment"
							/>
						</div>
						<button type="button" class="btn btn-primary" onClick= {e=>this.addDept(this.state.name)} >
							Save
						</button>
					</div>
				</div>

				<div className="col-sm-8 col-sm-offset-1 dptform">
					<Table>
						<table className="table table-bordered">
							<thead>
								<th scope="col">#</th>
								<th scope="col" colSpan="2">
									Department
								</th>
								<th scope="col">Action</th>
							</thead>
							<tbody>
								 {  this.state.list.map(item =>{
								 		return ( 
								 			<tr key={item.id}>
								 			<th scope="row" >1</th>
												<td colSpan="2">{item.name} </td>
												<td>
													<button type="button" class="btn btn-danger" onClick={deletDpt}>
														Delete
													</button>
												</td>
											</tr>
								 		)
								 	})
								 }
							</tbody>
						</table>
					</Table>
				</div>
			</Row>
		</div>
	</div>
	);
}
};


Department_List.propTypes = {};

export default Department_List;
