import React, { useState } from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';

const Department_List = () => {
	const Table = styled.div``;
	const Row = styled.div`margin-top: 10px;`;
	const HR = styled.hr`
	border: 0.125rem solid white;
	margin-top = 5px ;
	`;
	const table = styled.table`border: 1px solid black;`;
	const Label = styled.label`padding-top: 1rem;`;
	// set some values for department form
	const [ department, setDept ] = useState({
		newdpt: '',
		list: []
	});
	//destructure
	var { newdpt, list } = department;
	//handlechange
	const handleChange = (name) => (event) => {
		const value = event.target.value;
		setDept({ ...department, [name]: value });
	};
	//add department
	const addDepartment = () => {
		//destructure
		var { newdpt, list } = department;
		var newlist = list.push(newdpt);
		setDept({ ...department, list: newlist });
	};
	return (
		<div className="container">
			<Row className="row no-gutters">
				<div className="col-sm-2  dptform">
					<div class="cards">
						<div class="form-group">
							<Label for="comment">Department Form</Label>
							<HR />
							<h6>Name</h6>
							<input
								type="text"
								placeholder="Type..."
								value={newdpt}
								onChange={handleChange('newdpt')}
								class="form-control"
								rows="2"
								id="comment"
							/>
						</div>
						<button type="button" class="btn btn-primary" onClick={addDepartment}>
							Save
						</button>
					</div>
				</div>

				<div className="col-sm-8 col-sm-offset-1 dptform">
					<Table>
						<table className="table table-bordered">
							<thead>
								<th scope="col">#</th>
								<th scope="col" colSpan="2">
									Department
								</th>
								<th scope="col">Action</th>
							</thead>
							<tbody>
								<tr>
									{list.length > 0 &&
										list.map((name) => (
											<div>
												<th scope="row">1</th>
												<td colSpan="2">{name}</td>
												<td>
													<button type="button" class="btn btn-danger">
														Delete
													</button>
												</td>
											</div>
										))}
								</tr>
							</tbody>
						</table>
					</Table>
				</div>
			</Row>
		</div>
	);
};

Department_List.propTypes = {};

export default Department_List;
