import { API } from '../../backend';
const axios = require('axios')
export const AddDept = (name) => {
    const response = fetch('http://localhost:8000/api/adddepartment', {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
        body: JSON.stringify(name)
    });
    return response; 
}

export const delDept = (name) => {
    const reponse =  fetch('http://localhost:8000/api/deletedepartment', {
        method: "DELETE",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
        body: JSON.stringify(name)
    })
    return reponse.json();
};

export function getDepts () {
    return fetch('http://localhost:8000/api/getdept', {
        method: "GET",
    }).then(response => {
        console.log(response)
        return response.json();
    }).catch(err=> console.log(err))
} 

