import React, { useState, useEffect } from 'react';
import Sidebar from '../Core/Sidebar/sidebar';
import Header from '../Core/Headers/Headers';
import styled from 'styled-components';
import { getDepts, getPos } from '../Admin/helper/adminapicalls';
import PropTypes from 'prop-types';
const Form = styled.form`
	background-color: #e9e7e6;
	margin-left: 130px;
	margin-right: 100px;
	margin-top: 30px;
	border-radius: 10px;
`;
const Button = styled.button`margin-left: 40%;`;
const AddNew = (props) => {
	const [ employee, setEmployee ] = useState({
		name: '',
		email: '',
		phone: '',
		address: '',
		department: '',
		password: '',
		position: '',
		salary: false,
		departments: [],
		positions: []
	});
	//destructure employee
	var { name, email, phone, address, department, position, password, salary, departments, positions } = employee;
	//load values on load

	useEffect(() => {
		const loadDept = () => {
			//Learnt a new thing ->
			// Array value get changed back initial value when u go out of scope where u changed its value

			getDepts().then((dept) => {
				if (dept !== undefined) {
					getPos().then((pos) => {
						if (pos !== undefined) {
							//	console.log('ok')
							setEmployee({ ...employee, departments: dept, positions: pos });
						}
					});
				}
			});
		};
		// eslint-disable-next-line no-restricted-globals

		loadDept();
	}, []);
	const addNewForm = () => (
		<Form>
			<div className="addform">
				<label class="form-label">Full Name</label>
				<input
					type="text"
					class="form-control needs-validation"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Name"
					value={name}
				/>
				<div class="valid-feedback">Looks good!</div>
				<label class="form-label">Email</label>
				<input
					type="Email"
					class="form-control"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Email"
					value={email}
				/>
				<label class="form-label">Password</label>
				<input
					type="Text"
					class="form-control"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Password"
					value={password}
				/>
				<label class="form-label">Phone</label>
				<input
					type="text"
					class="form-control"
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Phone"
					value={phone}
				/>
				<label class="form-label">Address</label>
				<textarea class="form-control" placeholder="Address" id="floatingTextarea2" rows="3" value={address} />
				<select className="form-control" placeholder="Category">
					<option selected disabled>
						Department
					</option>
					{departments && departments.length > 0 ? (
						departments.map((dept, index) => <option key={index}>{dept.name}</option>)
					) : (
						<option>No Department Found</option>
					)}
				</select>
				<select className="form-control " placeholder="Category">
					<option selected disabled>
						Position
					</option>
					{positions && positions.length > 0 ? (
						positions.map((pos, index) => <option key={index}>{pos.name}</option>)
					) : (
						<option>No Position Found Found</option>
					)}
				</select>

				<br />
				<Button className="btn btn-success">Submit</Button>
			</div>
		</Form>
	);
	return (
		<div>
			<Header />
			<div className="row">
				<div className="col-sm-3">
					<Sidebar />
				</div>
				<div className="col-sm-7 ">{addNewForm()}</div>
			</div>
		</div>
	);
};

AddNew.propTypes = {};

export default AddNew;
