import React, { useState, useEffect } from 'react';
import Sidebar from '../Core/Sidebar/sidebar';
import Header from '../Core/Headers/Headers';
import styled from 'styled-components';
import { useForm } from 'react-hook-form';
import { getDepts, getPos } from '../Admin/helper/adminapicalls';
import PropTypes from 'prop-types';
const Form = styled.form`
	background-color: #e9e7e6;
	margin-left: 130px;
	margin-right: 100px;
	margin-top: 30px;
	border-radius: 10px;
`;
const Error = styled.span`color: red;`;
const Button = styled.button`margin-left: 40%;`;
const AddNew = (props) => {
	const { register, handleSubmit, errors } = useForm();
	const onSubmit = (data) => console.log(data);

	const [ employee, setEmployee ] = useState({
		salary: false,
		departments: [],
		positions: []
	});
	//destructure employee
	var { departments, positions } = employee;
	//load values on load

	useEffect(() => {
		const loadDept = () => {
			//Learnt a new thing ->
			// Array value get changed back initial value when u go out of scope where u changed its value

			getDepts().then((dept) => {
				if (dept !== undefined) {
					getPos().then((pos) => {
						if (pos !== undefined) {
							//	console.log('ok')
							setEmployee({ ...employee, departments: dept, positions: pos });
						}
					});
				}
			});
		};
		// eslint-disable-next-line no-restricted-globals

		loadDept();
	}, []);
	const addNewForm = () => (
		<Form onSubmit={handleSubmit(onSubmit)}>
			<div className="addform">
				<label class="form-label">Full Name</label>
				<input
					type="text"
					class="form-control "
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Name"
					name="name"
				/>
				<div class="invalid-feedback">Please choose a name</div>
				<label class="form-label">Email</label>
				<input
					type="Email"
					class="form-control "
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Email"
					name="email"
				/>
				<label class="form-label">Password</label>
				<input
					type="password"
					class="form-control "
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Password"
					name="password"
				/>
				<label class="form-label">Phone</label>
				<input
					type="tel"
					class="form-control "
					id="exampleInputEmail1"
					aria-describedby="emailHelp"
					placeholder="Phone"
					name="phone"
				/>
				<label class="form-label">Address</label>
				<textarea class="form-control " placeholder="Address" id="floatingTextarea2" rows="3" name="address" />
				<select className="form-control" name="department">
					<option selected disabled>
						Department
					</option>
					{departments && departments.length > 0 ? (
						departments.map((dept, index) => <option key={index}>{dept.name}</option>)
					) : (
						<option>No Department Found</option>
					)}
				</select>
				<select className="form-control " placeholder="Category">
					<option selected disabled>
						Position
					</option>
					{positions && positions.length > 0 ? (
						positions.map((pos, index) => <option key={index}>{pos.name}</option>)
					) : (
						<option>No Position Found Found</option>
					)}
				</select>

				<br />
				<Button className="btn btn-success">Submit</Button>
			</div>
		</Form>
	);
	return (
		<div>
			<Header />
			<div className="row">
				<div className="col-sm-3">
					<Sidebar />
				</div>
				<div className="col-sm-7 ">{addNewForm()}</div>
			</div>
		</div>
	);
};

AddNew.propTypes = {};

export default AddNew;
