import { API } from '../../backend';

export const AddDept = (name) => {
    return fetch('http://localhost:8000/api/adddepartment', {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
        body : JSON.stringify(name)
    }).then(response => {
        return response.json();
    }).catch(err=> console.log(err))
}

export const delDept = (name) => {
    return fetch(`${API}/deletedepartment`, {
        method: "DELETE",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
        body : JSON.stringify(name)
    }).then(response => {
        return response.json();
    }).catch(err=> console.log(err))
}

export const getDepts = (name) => {
    return fetch('http://localhost:8000/api/getdept', {
        method: "GET",
    }).then(response => {
        console.log('ok')
        return response.json();
    }).catch(err=> console.log(err))
}
